import React, { useEffect, useState } from 'react'
import Person from './models/Person'
import Add from './components/Add'
import ShowAll from './components/ShowAll'

const App = () => {
  const [people, setPeople] = useState<Person[]>([])
  useEffect(()=>{
    const person1 = new Person("Wallie", 32, 1)
    const person2 = new Person("Heshow", 32, 2)
    setPeople([person1, person2])
  },[])

  const addPeopleHandler = (name:string, age:number) =>{
    const newPerson = new Person(name, age, people.length)
    setPeople(prev=>{
      return {...prev, }
    })
  }

  console.log(people)
  return (
		<>
			<div>
				<h1>Testing passing props with typescript</h1>
			</div>
			<Add people={people} addPeopleHandler={addPeopleHandler} />
			<ShowAll />
		</>
	)
}

export default App
