const NewTodo=()=> {
    const todoRef = useRef()
    const [test, setTest] = useState('')

    const submitHandler = (e:React.FormEvent) =>{
        e.preventDefault();
        console.log(e)
    }

    console.log(test)

  return (
    <div>
        <form onSubmit={submitHandler}>
            <div>
                <label htmlFor='text'>Todo Text:
                    <input type="text" id='text'/>
                </label>
                <label htmlFor='test'>Todo Text:
                    <input type="text" id='test' onChange={(e:React.EventHandler)=>setTest(e.target.value)}/>
                </label>
            </div>
            <button type='submit'>Submit</button>
        </form>
    </div>
  )
}

export default NewTodo
