import React from 'react'

interface Name {
	firstName: string,
	lastName: string,
	fullName: string
}

const TestForm2:React.FC=()=>{
    const [name, setName] = React.useState<Name>({
			firstName: "",
			lastName: "",
			fullName: "",
		})


  return (
		<div>
			<h1>TestForm2</h1>
            <form onSubmit={()=>{}}>
                <div>
                <label>FirstName:
                    <input type="text" value={name.firstName} onChange={(e:React.ChangeEvent<HTMLInputElement>)=>{
                        setName({...name, firstName:e.target.value})
                    }}/>
                </label>
                </div>
            </form>
		</div>
	)
}

export default TestForm2
